import requests
import json

# Polymarket CLOB API endpoint
CLOB_URL = "https://clob.polymarket.com"

def fetch_order_book(token_id):
    """
    Fetch order book data for a specific token from Polymarket CLOB API.

    Args:
        token_id (str): The token ID to fetch order book for

    Returns:
        dict: Order book data or None if failed
    """
    book_url = f"{CLOB_URL}/book"
    params = {"token_id": token_id}

    try:
        response = requests.get(book_url, params=params)
        if response.status_code == 200:
            return response.json()
        else:
            print(f"Error fetching order book: {response.status_code}")
            return None
    except Exception as e:
        print(f"Exception fetching order book: {e}")
        return None

def get_active_markets():
    """
    Get list of active markets with order books.

    Returns:
        list: List of active markets or None if failed
    """
    clob_markets_url = f"{CLOB_URL}/markets"
    params = {"closed": False, "limit": 100}

    try:
        response = requests.get(clob_markets_url, params=params)
        if response.status_code == 200:
            return response.json()
        else:
            print(f"Error fetching markets: {response.status_code}")
            return None
    except Exception as e:
        print(f"Exception fetching markets: {e}")
        return None
